import { useState, useEffect } from "react";
import Button from "react-bootstrap/esm/Button";
import { DragDropContext } from "react-beautiful-dnd";
import {
  getPosts,
  initColumns,
  updateColumns,
  getAllColumns,
  initColumnOrder,
  getAllColumnOrder,
  updateColumnOrder,
} from "../Dashboard/postBehavior";
const LayoutDNDConnectDB = () => {
  const [state, setstate] = useState();
  const [columns, setColumns] = useState();
  const [orderColumns, setOrderColumns] = useState();
  const getallPost = async () => {
    try {
      const GetallPostzx = await getPosts(localStorage.getItem("token"));
      if (GetallPostzx.success) {
        setstate(GetallPostzx.Post_of_User);
      }
    } catch (error) {
      console.log(error);
    }
  };
  let title = {
    title: "Process",
  };

  const updatecolumnsinit = async () => {
    let UpdateInit = {
      _id: localStorage.getItem("IDinitColumns"),
      taskIds: [],
    };
    for (let index = 0; index < state.length; index++) {
      UpdateInit.taskIds.push(state[index]._id);
    }
    await updateColumns(UpdateInit);
  };

  const GetallCol = async () => {
    const gAllCol = await (await getAllColumns()).data.getALlCol;
    setColumns(gAllCol);
  };

  const getInitColumns = async () => {
    const getInit = await initColumns(title);
    localStorage.setItem("IDinitColumns", getInit.data.newcolumns._id);
    updatecolumnsinit();
    GetallCol();
    getInitColumnOrder();
  };

  const getInitColumnOrder = async () => {
    const getInit = await initColumnOrder();
    localStorage.setItem("IDinitColumnOrder", getInit.data.newOrder._id);
    updateOrderColumns();
    getAllorderColumn();
  };

  const updateOrderColumns = async () => {
    let UpdateInit = {
      _id: localStorage.getItem("IDinitColumnOrder"),
      columOrder: [],
    };
    for (let index = 0; index < state.length; index++) {
      UpdateInit.columOrder.push(state[index]._id);
    }
    await updateColumnOrder(UpdateInit);
  };

  const getAllorderColumn = async () => {
    const getALLOrder = (await getAllColumnOrder()).data.getALlColOrder;
    setOrderColumns(getALLOrder);
  };

  useEffect(() => {
    getallPost();
  });
  const onDragEnd = (results) => {};
  return (
    <>
      <Button onClick={getInitColumns}>init map</Button>
      {/* <DragDropContext></DragDropContext>
      {orderColumns.map((iteam) => {
        console.log("itemID", iteam);
        return (
          <form key={iteam._id}>
            <div>title : {iteam.title}</div>
            <div>content: </div>
          </form>
        );
      })} */}

      <DragDropContext onDragEnd={onDragEnd}>
        {console.log("orderColum",)}
        <div style={{ display: "flex" }}>
          {orderColumns &&
            orderColumns.map((columID) => {
              return <h1>haha</h1>;
            })}
        </div>
      </DragDropContext>
    </>
  );
};
export default LayoutDNDConnectDB;
