import React from "react";
import Button from "react-bootstrap/Button";
import ErrorBox from "./Errorbox";
import { Link } from "react-router-dom";
import { LoginUser } from "./UserBehavior";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Logo from "../../images/user.jpg";
export default function Login() {
  const [showErrorBox, setShowErrorBox] = useState(false);
  useEffect(() => {
    document.title = "Login";
  });

  const [loginForm, setLoginForm] = useState({
    username: "",
    password: "",
  });
  const nav = useNavigate();
  const onChangeLogin = (event) =>
    setLoginForm({ ...loginForm, [event.target.name]: event.target.value });
  setShowErrorBox(false);

  const Login = async (event) => {
    event.preventDefault();
    try {
      const LoginData = await LoginUser(loginForm);
      if (LoginData.success) {
        nav("/Dashboard");
      }
    } catch (error) {
      setShowErrorBox(true);
      console.log(error.response);
    }
  };

  return (
    <>
      <section class="ftco-section">
        <div class="container">
          <div class="row justify-content-center">
            <div class="col-md-6 text-center mb-5">
              <h2 class="heading-section">Login</h2>
            </div>
          </div>
          <div class="row justify-content-center">
            <div class="col-md-6 col-lg-5">
              <div class="login-wrap p-4 p-md-5 bg-light">
                <div class="icon d-flex align-items-center justify-content-center">
                  <img className="logo" src={Logo} alt="Logo" />
                </div>
                <h3 class="text-center mb-4">Have an account?</h3>
                <ErrorBox
                  messageError={"Thong tin sai"}
                  showErrorBox={showErrorBox}
                />
                <form action="#" class="login-form">
                  <div class="form-group">
                    <input
                      type="text"
                      class="form-control rounded-left mb-4 p-4"
                      placeholder="Username"
                      required=""
                      name={onChangeLogin}
                      name="username"
                    />
                  </div>
                  <div class="form-group d-flex">
                    <input
                      type="password"
                      class="form-control rounded-left mb-4 p-4"
                      placeholder="Password"
                      required=""
                    />
                  </div>
                  <div class="form-group d-md-flex">
                    <div class="w-50">
                      <label class="checkbox-wrap checkbox-primary">
                        Remember Me
                        <input type="checkbox" checked="" />
                        <span class="checkmark"></span>
                      </label>
                    </div>
                    <div class="w-50 text-md-right">
                      <Link to="/register">Register</Link>
                    </div>
                  </div>
                  <div class="form-group d-flex justify-content-between">
                    {/* <button
                      type="submit"
                      class="btn btn-primary rounded submit p-3 px-5"
                    >
                      Get Started
                    </button> */}
                    <Button className="m-6 w-100" onClick={Login}>
                      Sign in
                    </Button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
