import "./style.css";
import * as Icon from "react-bootstrap-icons";
import { sendemail } from "./emailBehavior";
import { useState } from "react";
import { CheckLoginUser, LoginUser, udlus } from "../Login/UserBehavior";
import { useNavigate } from "react-router-dom";
import {  decodeToken } from "react-jwt";
export const FormSendMail = ({ CodeShare, email }) => {
  let myDecodedToken = decodeToken(localStorage.getItem("CodeShare"));
  const nav = useNavigate();
  const [form, setform] = useState({
    _id: "",
    email: email,
    code: "",
  });

  const [user, setUser] = useState({});

  let FormAccessUser = {
    username: CodeShare.split("@")[1],
    password: CodeShare.split("@")[0],
  };

  let FormUpdateListUser = {
    _id: CodeShare.split("@")[2],
    ListEmailJoin: [],
    ListEmailRequestJoin: [],
  };
  const handlechange = (event) => {
    setform({ ...form, [event.target.name]: event.target.value });
  };
  const onstart = async () => {
    const LoginData = await CheckLoginUser(FormAccessUser);
    setUser(LoginData.user);
  };
  window.onload = function () {
    onstart();
  };
  onstart();
  const startSendMail = async (event) => {
    //check List user
    

    FormUpdateListUser.ListEmailJoin = Array.from(user.ListEmailJoin);
    FormUpdateListUser.ListEmailRequestJoin = Array.from(
      user.ListEmailRequestJoin
    );
    try {
      if (FormUpdateListUser.ListEmailRequestJoin.indexOf(`${email}`) < 0) {
        FormUpdateListUser.ListEmailRequestJoin.push(`${email}`);
        FormUpdateListUser.ListEmailJoin.push("luancafesuaaa@gmail.com");
        await udlus(FormUpdateListUser);
        // await sendemail(form);
        alert("Email has been sent request !!");
      }
      if (FormUpdateListUser.ListEmailJoin.indexOf(`${email}`) > -1) {
        localStorage.clear();
        await LoginUser(FormAccessUser);
        nav("/login");
      }
    } catch (error) {
      console.log(error.response);
    }
    event.preventDefault();
  };
  return (
    <>
      <button
        type="button"
        data-bs-toggle="collapse"
        data-bs-target="#collapseExample"
        aria-expanded="false"
        aria-controls="collapseExample"
        class="btn btn-info btn-circle btn-xl btnCollapse"
      >
        <Icon.ListCheck></Icon.ListCheck>
      </button>

      <div class="CollapseForm collapse" id="collapseExample">
        <div className="cssforform-Body">
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">
              Email address {FormAccessUser}
            </label>
            <input
              type="email"
              name="email"
              onChange={handlechange}
              value={form.email}
              class="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
          </div>
          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">
              CODE
            </label>
            <input
              onChange={handlechange}
              value={form.code}
              name="code"
              type="text"
              class="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <button onClick={startSendMail} class="btn btn-primary">
            Send
          </button>
        </div>
      </div>
    </>
  );
};
