import React from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Link } from "react-router-dom";
// import { LoginUser } from "./UserBehavior";
import { useState, useEffect } from "react";
import Axios from "axios";
export default function Login() {
  useEffect(() => {
    document.title = "Login";
  });

  const [loginForm, setLoginForm] = useState({
    username: "",
    password: "",
  });

  const onChangeLogin = (event) => {
    setLoginForm({ ...loginForm, [event.target.name]: event.target.value });
  };
  // const Login = async (event) => {
  //   try {
  //     const LoginData = await LoginUser(loginForm);
  //     console.log("Logindata", LoginData);
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };
  const LoginUser = async (req) => {
    const respod = await Axios.get("http://localhost:8080/Auth/Login", req);
    console.log("respond", respod);
    if (respod.data.success) {
      localStorage.setItem("token", respod.Accesstoken);
    }
    console.log(respod.data); 
  };
  return (
    <>
      <Form onSubmit={LoginUser}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>username</Form.Label>
          <Form.Control
            name="username"
            onChange={onChangeLogin}
            value={loginForm.username}
            type="text"
            placeholder="Enter email"
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            name="password"
            onChange={onChangeLogin}
            value={loginForm.password}
            type="password"
            placeholder="Password"
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>
        <Link to="/register">
          <Button>register</Button>
        </Link>
        <Button onClick={Login}>test</Button>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </>
  );
}
