import "./style.css";
import * as Icon from "react-bootstrap-icons";
import { sendemail } from "./emailBehavior";
import { useState } from "react";
import { LoginUser } from "../Login/UserBehavior";
export const FormSendMail = ({ CodeShare }) => {
  const [form, setform] = useState({
    email: "",
    code: "",
  });
  let FormAccessUser = {
    username:CodeShare.split("@")[1],
    password:CodeShare.split("@")[0]
  }
  let code = CodeShare;
  const Login = async (event) => {
    event.preventDefault();
    try {
      const LoginData = await LoginUser(FormAccessUser);
      if (LoginData.success) {
        nav("/Dashboard");
      }
    } catch (error) {
      setShowErrorBox(true);
      console.log(error.response);
    }
  };

  const handlechange = (event) => {
    setform({ ...form, [event.target.name]: event.target.value });
  };
  const startSendMail = async (event) => {
    event.preventDefault();
    await sendemail(form);
  };
  return (
    <>
      <button
        type="button"
        data-bs-toggle="collapse"
        data-bs-target="#collapseExample"
        aria-expanded="false"
        aria-controls="collapseExample"
        class="btn btn-info btn-circle btn-xl btnCollapse"
      >
        <Icon.ListCheck></Icon.ListCheck>
      </button>

      <div class="CollapseForm collapse" id="collapseExample">
        <div className="cssforform-Body">
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">
              Email address {code}
            </label>
            <input
              type="email"
              name="email"
              onChange={handlechange}
              value={form.email}
              class="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
          </div>
          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">
              CODE
            </label>
            <input
              onChange={handlechange}
              value={form.code}
              name="code"
              type="text"
              class="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <button onClick={startSendMail} class="btn btn-primary">
            Send
          </button>
        </div>
      </div>
    </>
  );
};
