  // {lg: layout1, md: layout2, ...}
  const [layout1, setlayout1] = useState([
    {
      nameOflayout: "avc",
      layout: null,
    },
  ]);

  const onchangeLayout = (event,layoutItem) => {
    setlayout1({ ...layout1, layout:layout1.layout({
        i:"new name",
        x:layoutItem.x,
        y:layoutItem.y
    })});
  };

