    { i: "b", x: 1, y: 1, w: 1, h: 2 },
    { i: "c", x: 2, y: 1, w: 1, h: 2, moved: false },
  ];
  return (
    <>
      <div
        style={{
          display: "flex",
          width: "100%",
          justifyContent: "space-between",
        }}
      >
        <GridLayout
          className="layout"
