import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { Navbar } from "../Layout/Navbar";
import { Map } from "../Layout/Caroucel";
import Button from "react-bootstrap/esm/Button";
import React from "react";
import { NotificationContainer } from "react-notifications";
import { createNotification } from "../Notifications/notifications";
import "react-notifications/lib/notifications.css";
import {
  getPosts,
  getPostsByMapId,
  createnewPost,
  DeletedPost,
  getOnePost,
  EditPost,
} from "./postBehavior";
import { udlus } from "../Login/UserBehavior";
import { CheckLoginUser } from "../Login/UserBehavior";
import ReactTooltip from "react-tooltip";
import { ModalEdit, ModalAddpost } from "./handleDash";
import { FormSendMail } from "../SendEmail/Sendmail";
import { decodeToken } from "react-jwt";
import "./style.css";
import * as Icon from "react-bootstrap-icons";
export const Dashboard = () => {
  const [Post, setPost] = useState();
  const [Posttemp, setPosttemp] = useState();

  const [clickOnetimes, setClickOnetimes] = useState(false);
  const [ListUserAccess, setListUserAccess] = useState([]);
  const [ListUserRequest, setListUserRequest] = useState([]);
  let myDecodedToken = decodeToken(localStorage.getItem("CodeShare"));
  window.onload = function () {
    getListfromUser();
  };
  let nav = useNavigate();

  const getListfromUser = async () => {
    let FormAccessUser = {
      username: myDecodedToken.CODESHARE.split("#")[1],
      password: myDecodedToken.CODESHARE.split("#")[0],
    };
    const getUser = await CheckLoginUser(FormAccessUser);
    getUser.user.ListEmailJoin.then(function (result) {  
     setListUserAccess(result);
    });
    getUser.user.ListEmailRequestJoin.then(function (result) {  
    // setListUserRequest(result);
    });
    
  };
  const [createPost, setCreatePost] = useState({
    title: "",
    Des: "",
    Url: "",
    Behavior: "Learning",
    color: "#ffa500",
    user: localStorage.getItem("userid"),
    MapId: localStorage.getItem("mapid"),
  });

  // Edit Post
  let [getPostEdit, setGetPostEdit] = useState({});

  const onchangeEdit = (event) => {
    setGetPostEdit({
      ...getPostEdit,
      [event.target.name]: event.target.value,
    });
  };

  const onSubmitEdit = async (res) => {
    try {
      const GetEdit = await EditPost(getPostEdit);
      console.log("Edt success", GetEdit);
      if (GetEdit.data.success) {
        createNotification("warning", GetEdit.data.Message);
      }
    } catch (error) {
      createNotification("error", "Title can't be empty");
    }
  };

  const onchangeCreate = (event) => {
    setCreatePost({
      ...createPost,
      [event.target.name]: event.target.value,
    });
  };

  const getallPost = async () => {
    try {
      const GetallPostzx = await getPosts(localStorage.getItem("token"));

      if (GetallPostzx.success) {
        setPost(GetallPostzx.Post_of_User);
        // setPosttemp(GetallPostBYmapId.Post_of_User);
      }
    } catch (error) {
      console.log(error);
    }
  };
  const getPostBymapid = async () => {
    try {
      const GetallPostzx = await getPostsByMapId(localStorage.getItem("mapid"));

      if (GetallPostzx.success) {
        setPost(GetallPostzx.Post_of_User);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const onclickGetonePost = async (event) => {
    const getOneP = await getOnePost(event.target.value);
    console.log("get one post", getOneP);
    if (getOneP.success) {
      setGetPostEdit(getOneP.posts);
      getPostEdit = getOneP.posts;
    }
  };

  const onSubmitCreate = async (event) => {
    event.preventDefault();
    try {
      const onCreate = await createnewPost(createPost);
      console.log("create post", onCreate);
      if (onCreate.data.success) {
        createNotification("success", "Create new post success");
      }
    } catch (error) {
      createNotification("error", "Title can't be empty");
    }
  };
  const RemovePost = async (event) => {
    await DeletedPost(event.target.value);
  };
  useEffect(() => {
    if (!localStorage.getItem("token")) {
      return nav("/login");
    }

    getPostBymapid();
    getListfromUser();
  });
  const RemoveUserAccess = async (event) => {
    let layremove = [];
    layremove = ListUserAccess;
    const index = layremove.indexOf(event.target.value);
    if (index > -1) {
      layremove.splice(index, 1);
    }
    let FormUpdateListUser = {
      _id: localStorage.getItem("userid"),
      ListEmailJoin: layremove,
    };
    await udlus(FormUpdateListUser);

    getListfromUser();
  };
  const AcceptUserFromRequest = async (event) => {
    let laypop = [];
    laypop = ListUserRequest;
    const index = laypop.indexOf(event.target.value);
    if (index > -1) {
      laypop.splice(index, 1);
    }
    let laypush = [];
    laypush = ListUserAccess;
    laypush.push(`${event.target.value}`);
    let FormUpdateListUser = {
      _id: localStorage.getItem("userid"),
      ListEmailJoin: laypush,
      ListEmailRequestJoin: laypop,
    };
    await udlus(FormUpdateListUser);

    getListfromUser();
  };
  const RemoveUserFromRequest = async (event) => {
    let layremove = [];
    layremove = ListUserRequest;
    const index = layremove.indexOf(event.target.value);
    if (index > -1) {
      layremove.splice(index, 1);
    }
    let FormUpdateListUser = {
      _id: localStorage.getItem("userid"),
      ListEmailRequestJoin: layremove,
    };
    await udlus(FormUpdateListUser);

    getListfromUser();
  };
  const gobackHomepage = () => {
    nav("/homepage");
    window.location.reaload();
  };
  return (
    <>
      {localStorage.getItem("managerUser") === "true" ? (
        <FormSendMail
          CodeShare={myDecodedToken.CODESHARE}
          email={localStorage.getItem("email")}
          setListUserAccess={setListUserAccess}
          setListUserRequest={setListUserRequest}
          getListfromUser={getListfromUser()}
        ></FormSendMail>
      ) : (
        ""
      )}

      <Navbar></Navbar>

      <NotificationContainer></NotificationContainer>
      <div style={{ display: "flex", marginTop: "30px" }}>
        <div className="managePost">
          <span style={{ marginLeft: "12px" }} className="managePost-title">
            <Button
              type="button"
              className="btn btn-primary"
              data-bs-toggle="modal"
              data-bs-target="#staticBackdrop"
            >
              Add post
            </Button>
            <Button
              type="button"
              className="btn btn-primary"
              style={{ marginLeft: "20px" }}
              onClick={gobackHomepage}
            >
              Back
            </Button>

            <a style={{ width: "100%", marginLeft: "20px" }} href="/layoutdb">
              <Button>Go to work-space</Button>
            </a>
            {localStorage.getItem("managerUser") === "true" ? (
              <Button
                style={{ marginLeft: "20px" }}
                className="btn btn-primary"
                type="button"
                data-bs-toggle="offcanvas"
                data-bs-target="#offcanvasScrolling"
                aria-controls="offcanvasScrolling"
              >
                Manager user
              </Button>
            ) : (
              ""
            )}

            <div
              className="offcanvas offcanvas-start"
              data-bs-scroll="true"
              data-bs-backdrop="false"
              tabIndex="-1"
              id="offcanvasScrolling"
              aria-labelledby="offcanvasScrollingLabel"
            >
              <div className="offcanvas-header">
                <h5 className=" offcanvas-title" id="offcanvasScrollingLabel">
                  User management
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="offcanvas"
                  aria-label="Close"
                ></button>
              </div>

              <div className="offcanvas-body">
                <div className="FormUserAccess">
                  <div className="FormUserAccess-title">
                    <h5> list of users with access rights</h5>
                  </div>
                  <ul className="FormUserAccess-list">
                    {ListUserAccess &&
                      ListUserAccess.map((ListUserAccess) => (
                        <li className="FormUserAccess-list-iteam">
                          <div className="FormUserAccess-list-iteam_left">
                            {ListUserAccess}
                          </div>
                          <div className="FormUserAccess-list-iteam_Right">
                            <button
                              value={ListUserAccess}
                              onClick={RemoveUserAccess}
                              type="button"
                              className="btn btn-danger mt-1"
                            >
                              x
                            </button>
                          </div>
                        </li>
                      ))}
                  </ul>
                </div>
                <hr></hr>
                <div className="FormUserRequestAccess">
                  <div className="FormUserRequestAccess-title">
                    <h5>Email sent approval request</h5>
                  </div>
                  <ul className="FormUserRequestAccess-list">
                    {ListUserRequest &&
                      ListUserRequest.map((ListUserRequest) => (
                        <li className="FormUserRequestAccess-list-iteam">
                          <div className="FormUserRequestAccess-list-iteam_left">
                            {ListUserRequest}
                          </div>
                          <div className="FormUserRequestAccess-list-iteam_Right">
                            <button
                              value={ListUserRequest}
                              onClick={AcceptUserFromRequest}
                              type="button"
                              className="btn btn-success mt-1"
                            >
                              âœ”
                            </button>
                            &ensp;
                            <button
                              value={ListUserRequest}
                              onClick={RemoveUserFromRequest}
                              type="button"
                              className="btn btn-danger mt-1"
                            >
                              x
                            </button>
                          </div>
                        </li>
                      ))}
                  </ul>
                </div>
              </div>
            </div>
            {localStorage.getItem("managerUser") === "true" ? (
              <Button
                data-tip="Coppy code share"
                className="btn-info"
                onClick={() => {
                  const copyText = document.getElementById("myInput");
                  navigator.clipboard.writeText(copyText.value);
                  setClickOnetimes(true);
                }}
                style={{ marginLeft: "20px" }}
              >
                <ReactTooltip style={{ marginLeft: "50px" }} />
                {clickOnetimes ? (
                  <Icon.Check size={25}></Icon.Check>
                ) : (
                  <Icon.Clipboard2 size={25}></Icon.Clipboard2>
                )}
              </Button>
            ) : (
              ""
            )}

            <input
              type="hidden"
              value={localStorage.getItem("CodeShare")}
              id="myInput"
            ></input>
          </span>
          <ModalAddpost
            onchangeCreate={onchangeCreate}
            createPost={createPost}
            onSubmitCreate={onSubmitCreate}
          ></ModalAddpost>
          <form>
            <div className="managePost-body">
              <ul className="body-Listitem">
                {Post &&
                  Post.map(({ title, _id, Url, Des, user, color }) => (
                    <li
                      key={_id}
                      id={_id}
                      name="id"
                      style={{ backgroundColor: `${color}` }}
                      className="body-item"
                    >
                      <Button onClick={RemovePost} value={_id}>
                        X
                      </Button>
                      <Button
                        type="button"
                        className="btn btn-primary"
                        data-bs-toggle="modal"
                        data-bs-target="#staticBackdrop2"
                        onClick={onclickGetonePost}
                        value={_id}
                      >
                        Edit
                      </Button>
                      <Button
                        onClick={() => {
                          window.open(Url);
                        }}
                      >
                        ðŸ”—
                      </Button>
                      <ModalEdit
                        id={_id}
                        color={color}
                        onchangeEdit={onchangeEdit}
                        onSubmitEdit={onSubmitEdit}
                        getPostEdit={getPostEdit}
                      ></ModalEdit>

                      <p className="cssforItem-content">
                        <b>Title: {title}</b>
                      </p>
                      <p className="cssforItem-content">
                        URL:
                        {Url && Url.length > 20
                          ? `${Url.substr(0, 20)}...`
                          : `${Url}`}
                      </p>
                      <p className="cssforItem-content">
                        DES:
                        {Des && Des.length > 20
                          ? `${Des.substr(0, 19)}...`
                          : `${Des}`}
                      </p>
                    </li>
                  ))}
              </ul>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};
