import axios from "axios";

export const sendemail = async (req, res) => {
  console.log("form", req);
  const response = await axios.post("http://localhost:8080/sendgmail", req, {
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  return response;
};
