import { Navbar } from "../Layout/Navbar";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getAllMaps, getAllByMapId, createMaps } from "./HomeBehavior";
import "./style.css";
import Button from "react-bootstrap/esm/Button";
import { FormSendMail } from "../SendEmail/Sendmail";
export const HomePage = () => {
  let nav = useNavigate();
  const [GetAllmap, setGetAllMap] = useState([]);
  const [FormBoard, setFormBoard] = useState({});

  const getAllMapBYid = async () => {
    try {
      const getMap = await getAllByMapId(localStorage.getItem("userid"));
      if (getMap.success) {
        setGetAllMap(getMap.mapgetall);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const onHandleChange = (event) => {
    setFormBoard({
      ...FormBoard,

      [event.target.name]: event.target.value,
    });
  };
  const CreateBoard = async () => {
    await createMaps(FormBoard);
  };
  const GetIdMapAndRedirect = (event) => {
    localStorage.setItem("mapid", event.target.value);
    nav("/Dashboard");
    window.location.reload();
    return;
  };
  useEffect(() => {
    if (!localStorage.getItem("token")) {
      return nav("/login");
    }
    getAllMapBYid();
  });
  return (
    <>
      <Navbar></Navbar>
      <div
        style={{ position: "fixed", top: "13%", left: "2%" }}
        class="btn-group dropend"
      >
        <button
          type="button"
          class="btn btn-secondary dropdown-toggle"
          data-bs-toggle="dropdown"
          aria-expanded="false"
        >
          Create Board
        </button>
        <ul style={{ padding: "0px 17px", top: "-23px" }} class="dropdown-menu">
          <div>
            <div class="mb-3">
              <label
                style={{ marginLeft: "34%" }}
                for="exampleInputEmail1"
                class="form-label"
              >
                Title
              </label>
              <input
                type="text"
                name="name"
                onChange={onHandleChange}
                class="form-control"
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
              />
            </div>
            <button
              style={{ marginLeft: "15%" }}
              class="btn btn-primary"
              onClick={CreateBoard}
            >
              Submit
            </button>
          </div>
        </ul>
        {localStorage.getItem("managerUser") === "true" ? (
        <FormSendMail
          CodeShare={myDecodedToken.CODESHARE}
          email={localStorage.getItem("email")}
          // setListUserAccess={}
          // setListUserRequest={}
          getListfromUser={getListfromUser()}
        ></FormSendMail>
      ) : (
        ""
      )}
      </div>
      <div>
        <div className="shadow cssforUl">
          {GetAllmap &&
            GetAllmap.map((item, index) => (
              <div class="shadow cssforUl-li card">
                <img
                  class="card-img-top"
                  src="https://www.gettyimages.in/gi-resources/images/Homepage/Hero/UK/CMS_Creative_164657191_Kingfisher.jpg"
                  alt=""
                />
                <div class="card-body">
                  <h5 class="card-title" style={{ color: "black" }}>
                    {GetAllmap[index].name}
                  </h5>
                  <Button
                    class="btn btn-primary"
                    value={GetAllmap[index]._id}
                    onClick={GetIdMapAndRedirect}
                  >
                    Go To Board
                  </Button>
                  <Button
                    class="btn btn-danger"
                    style={{ marginLeft: "20px" }}
                    value={GetAllmap[index]._id}
                    onClick={GetIdMapAndRedirect}
                  >
                    Delete Map
                  </Button>
                </div>
              </div>
            ))}
        </div>
      </div>
    </>
  );
};
