import React from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Link } from "react-router-dom";
import { LoginUser } from "./UserBehavior";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Logo_user from "./user.png";

export default function Login() {
  useEffect(() => {
    document.title = "Login";
  });

  const [loginForm, setLoginForm] = useState({
    username: "",
    password: "",
  });
  const nav = useNavigate();
  const onChangeLogin = (event) =>
    setLoginForm({ ...loginForm, [event.target.name]: event.target.value });

  const Login = async (event) => {
    event.preventDefault();
    try {
      const LoginData = await LoginUser(loginForm);
      if (LoginData.success) {
        nav("/Dashboard");
      }
    } catch (error) {
      console.log(error.response);
    }
  };

  return (
    <>
      {/* <Form onSubmit={Login}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>username</Form.Label>
          <Form.Control
            name="username"
            onChange={onChangeLogin}
            value={loginForm.username}
            type="text"
            placeholder="Enter email"
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            name="password"
            onChange={onChangeLogin}
            value={loginForm.password}
            type="password"
            placeholder="Password"
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>
        <Link to="/register">
          <Button>register</Button>
        </Link>
        <Button onClick={Login}>Submit</Button>
      </Form> */}
      <div class="form-bg">
    <div class="container">
        <div class="row">
            <div class="col-md-offset-4 col-md-4 col-sm-offset-3 col-sm-6">
                <div class="form-container">
                    <form class="form-horizontal">
                        <h3 class="title">Login Form</h3>
                        <div class="form-group">
                            <span class="input-icon"><i class="fa fa-user"></i></span>
                            <input class="form-control" type="email" placeholder="Username"/>
                        </div>
                        <div class="form-group">
                            <span class="input-icon"><i class="fa fa-lock"></i></span>
                            <input class="form-control" type="password" placeholder="Password"/>
                        </div>
                        <button class="btn signin">Log in</button>
                        <span class="forgot-pass"><a href="#">Lost password?</a></span>
                        <span class="register"><a href="#">Register / Signup</a></span>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
    </>
  );
}
