import { useState, useEffect } from "react";
import Button from "react-bootstrap/esm/Button";
import { DragDropContext } from "react-beautiful-dnd";
import {
  getPosts,
  initColumns,
  updateColumns,
  getAllColumns,
  DeletedColumns_ByID,
} from "../Dashboard/postBehavior";
import Column from "./Column";
const LayoutDNDConnectDB = () => {
  const [state, setstate] = useState([]);
  const [columns, setColumns] = useState([]);
  const getallPost = async () => {
    try {
      const GetallPostzx = await getPosts(localStorage.getItem("token"));
      if (GetallPostzx.success) {
        setstate(GetallPostzx.Post_of_User);
      }
    } catch (error) {
      console.log(error);
    }
  };
  let title = {
    title: "Process",
  };
  const updatecolumnsinit = async () => {
    let UpdateInit = {
      _id: localStorage.getItem("IDinitColumns"),
      taskIds: [],
    };
    for (let index = 0; index < state.length; index++) {
      UpdateInit.taskIds.push(state[index]._id);
    }
    await updateColumns(UpdateInit);
  };

  const GetallCol = async () => {
    const gAllCol = await (await getAllColumns()).data.getALlCol;
    setColumns(gAllCol);
  };

  const getInitColumns = async () => {
    console.log("Id init ", localStorage.getItem("IDinitColumns"));
    if (localStorage.getItem("IDinitColumns") !== null) {
      DeltedById(localStorage.getItem("IDinitColumns"));
      const getInit = await initColumns(title);
      localStorage.setItem("IDinitColumns", getInit.data.newcolumns._id);
      updatecolumnsinit();
      GetallCol();
    } else {
      const getInit = await initColumns(title);
      localStorage.setItem("IDinitColumns", getInit.data.newcolumns._id);
      updatecolumnsinit();
      GetallCol();
    }
  };
  const DeltedById = async (id) => {
    console.log("id delted", id);
    const ShowDelted = await DeletedColumns_ByID(id);
  };
  useEffect(() => {
    GetallCol();
    getallPost();
  });
  const onDragEnd = (results) => {
    const { destination, draggableId, source } = results;
    if (!destination) {
      return;
    }
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }
    console.log("Start", start, columns, source.droppableId);
    console.log("finish", finish, columns, destination.droppableId);

    const start = columns[source.droppableId];
    const finish = columns[destination.droppableId];

    console.log("Start", start, columns, source.droppableId);
    console.log("finish", finish, columns, destination.droppableId);

    if (start === finish) {
      const newTaskID = Array.from(start.taskIds);

      newTaskID.splice(source.index, 1);
      newTaskID.splice(destination.index, 0, draggableId);

      const newcolumn = {
        ...start,
        taskIds: newTaskID,
      };
      const newstate = {
        ...state,
        colums: {
          ...state.colums,
          [newcolumn.id]: newcolumn,
        },
      };
      // console.log("Result start == finish", results);
      setstate(newstate);
      // console.log("rs", results);
      return;
    }

    const starttaskID = Array.from(start.taskIds);
    starttaskID.splice(source.index, 1);
    const newstart = {
      ...start,
      taskIds: starttaskID,
    };

    const finishtaskID = Array.from(finish.taskIds);
    finishtaskID.splice(destination.index, 0, draggableId);
    const newfinish = {
      ...finish,
      taskIds: finishtaskID,
    };

    const newstate = {
      ...state,
      colums: {
        ...state.colums,
        [newstart.id]: newstart,
        [newfinish.id]: newfinish,
      },
    };
    console.log("Result", results);
    setstate(newstate);
  };
  return (
    <>
      <Button onClick={getInitColumns}>init map</Button>
      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: "flex" }}>
          {columns &&
            columns.map((columID, index) => {
              const colum = columns[index];
              const task = colum.taskIds.map(
                (taskIds, index) => colum.taskIds[index]
              );
              if (task[0] === undefined) {
                const lay = [];
                return (
                  <Column key={colum.id} column={colum} tasks={lay}></Column>
                );
              } else {
                return (
                  <Column
                    key={colum.id}
                    column={colum}
                    tasks={task[0]}
                  ></Column>
                );
              }
            })}
        </div>
      </DragDropContext>
    </>
  );
};
export default LayoutDNDConnectDB;
