import { Responsive as ResponsiveGridLayout } from "react-grid-layout";
import GridLayout from "react-grid-layout";
import RGL, { WidthProvider } from "react-grid-layout";
import "./style.css";

import { useEffect, useState } from "react";
const ReactGridLayout = WidthProvider(RGL);
export const Layoutgrid = () => {
  // {lg: layout1, md: layout2, ...}
  const [layout1, setState] = useState([
    {
      nameOflayout: "avc",
      layout: [
        {
          x: Number,
          y: Number,
        },
      ],
    },
  ]);

  const onLayoutChange = (layout) => {
    setlayout1({ layout });
    this.props.onLayoutChange(layout); // updates status display
  }

  useEffect(() => {
    setlayout1({ nameOflayout: "layout so 1", layout: layout });
    console.log("layout", layout1.layout);
  });

  const layout = [
    { i: "a", x: 0, y: 2, w: 2, h: 2 },
    { i: "b", x: 1, y: 2, w: 1, h: 2 },
    { i: "c", x: 4, y: 0, w: 1, h: 2, moved: false },
  ];

  return (
    <>
      <div>
        <button onClick={this.resetLayout}>Reset Layout</button>
        <ReactGridLayout
          {...this.props}
          layout={this.state.layout}
          onLayoutChange={this.onLayoutChange}
        >
          <div key="1" data-grid={{ w: 2, h: 3, x: 0, y: 0 }}>
            <span className="text">1</span>
          </div>
          <div key="2" data-grid={{ w: 2, h: 3, x: 2, y: 0 }}>
            <span className="text">2</span>
          </div>
          <div key="3" data-grid={{ w: 2, h: 3, x: 4, y: 0 }}>
            <span className="text">3</span>
          </div>
          <div key="4" data-grid={{ w: 2, h: 3, x: 6, y: 0 }}>
            <span className="text">4</span>
          </div>
          <div key="5" data-grid={{ w: 2, h: 3, x: 8, y: 0 }}>
            <span className="text">5</span>
          </div>
        </ReactGridLayout>
      </div>
    </>
  );
};
