import { Draggable } from "react-beautiful-dnd";
import styled from "styled-components";
import { getOnePost } from "../Dashboard/postBehavior";
const Container = styled.div`
  padding: 30px;
  border: solid 2px;
  display: flex;
  background-color: ${(props) => (props.isDragging ? "blue" : "white")};
`;
const Handle = styled.div`
  background-color: orange;
  width: 43px;
  height: 28px;
  border-radius: 13px;
  margin-right: 20px;
`;
const onclickGetonePost = async ( task ) => {
  const getOneP = await getOnePost(task);
  console.log("Get id post", getOneP.data);
  console.log("Get one post", getOneP.data);
};
const Task = ({ task, index }) => {
  let taskname;
  onclickGetonePost(task);
  return (
    <>
      {/* {console.log("task dnd.id.tostring", task)}
      {console.log("index", index)} */}
      <Draggable draggableId={task} index={index}>
        {(provider, snapshot) => (
          <Container
            {...provider.draggableProps}
            isDragging={snapshot.isDragging}
            ref={provider.innerRef}
          >
            <Handle {...provider.dragHandleProps}></Handle>
            {task}
          </Container>
        )}
      </Draggable>
    </>
  );
};
export default Task;
