import axios from "axios";

export const getPosts = async (req, res) => {
  const response = await axios.get("http://localhost:8080/Post/getAllpost", {
    headers: { Authorization: `Bearer ${req}` },
  });
  if (response.data.success) {
  }
  return response.data;
};

export const getPostsByMapId = async (req, res) => {
  const response = await axios.get(
    `http://localhost:8080/Post/GetPostsOfUserByMapid/${req}`,
    {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    }
  );
  return response.data;
};
export const createnewPost = async (req, res) => {
  const response = await axios.post("http://localhost:8080/Post/", req, {
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });

  return response;
};

export const DeletedPost = async (req, res) => {
  const response = await axios.delete(`http://localhost:8080/Post/${req}`, {
    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
  });
  return response.data;
};

export const getOnePost = async (req, res) => {
  const response = await axios.post(
    `http://localhost:8080/Post/getOnePost/${req}`,
    {
      params: {
        _id: `${req}`,
      },
    },
    {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    }
  );
  return response.data;
};

export const EditPost = async (req, res) => {
  const response = await axios.put(
    `http://localhost:8080/Post/${req._id}`,
    req,
    {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    }
  );
  return response;
};

//init columns

export const initColumns = async (req, res) => {
  const response = await axios.post(`http://localhost:8080/columns`, req, {
