import React from "react";
import Button from "react-bootstrap/Button";

import { useNavigate, Link } from "react-router-dom";
import { useState } from "react";
import { RegisterUser } from "./UserBehavior";
import ErrorBox from "./Errorbox";
import Logo from "../Login/user.jpg";
const Register = () => {
  const [showErrorBox, setShowErrorBox] = useState(false);
  let nav = useNavigate();
  const [formRegister, changeRegister] = useState({
    username: "",
    password: "",
    repassword: "",
  });
  const handleRegister = (event) => {
    changeRegister({
      ...formRegister,
      [event.target.name]: event.target.value,
    });
    setShowErrorBox(false);
  };
  const register = async () => {
    if (formRegister.password !== formRegister.repassword) {
      return setShowErrorBox(true);
    }
    try {
      const registerUser = await RegisterUser(formRegister);
      if (registerUser.success) {
        nav("/login");
      }
    } catch (error) {
      setShowErrorBox(true);
      console.log(error.response);
    }
  };
  return (
    <>
      <div class="overbox">
          <div class="material-button alt-2">
            <span class="shape"></span>
          </div>

          <div class="title">REGISTER</div>
          <ErrorBox
                  messageError={"Thông tin không hợp lệ"}
                  showErrorBox={showErrorBox}
                />
          <div class="input">
            <label for="regname">Username</label>
            <input type="text" name="regname" id="regname" />
            <span class="spin"></span>
          </div>

          <div class="input">
            <label for="regpass">Password</label>
            <input type="password" name="regpass" id="regpass" />
            <span class="spin"></span>
          </div>

          <div class="input">
            <label for="reregpass">Repeat Password</label>
            <input type="password" name="reregpass" id="reregpass" />
            <span class="spin"></span>
          </div>

          <div class="button">
            <button>
              <span>NEXT</span>
            </button>
          </div>
        </div>
    </>
  );
};
export default Register;
