import React from "react";
import Button from "react-bootstrap/Button";

import { useNavigate, Link } from "react-router-dom";
import { useState } from "react";
import { RegisterUser } from "./UserBehavior";
import ErrorBox from "./Errorbox";
import Logo from "../../images/user.jpg";
const Register = () => {
  const [showErrorBox, setShowErrorBox] = useState(false);
  let nav = useNavigate();
  const [formRegister, changeRegister] = useState({
    username: "",
    password: "",
    repassword: "",
  });
  const handleRegister = (event) => {
    changeRegister({
      ...formRegister,
      [event.target.name]: event.target.value,
    });
    setShowErrorBox(false);
  };
  const register = async () => {
    try {
      const registerUser = await RegisterUser(formRegister);
      if (registerUser.success) {
        return nav("/login");
      }
    } catch (error) {
      setShowErrorBox(true);
      console.log(error.response);
    }
  };
  return (
    <>
      {/* <h1>Register</h1>
      <ErrorBox messageError={"Thong tin sai"} showErrorBox={showErrorBox} />
