        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>username</Form.Label>
          <Form.Control
            name="username"
            onChange={handleRegister}
            value={formRegister.username}
            type="text"
            placeholder="Enter email"
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            name="password"
            onChange={handleRegister}
            value={formRegister.password}
            type="password"
            placeholder="Password"
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Re-Password</Form.Label>
          <Form.Control
            name="repassword"
            onChange={handleRegister}
            value={formRegister.repassword}
            type="password"
            placeholder="Password"
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>
        <Link to="/login">
          <Button>register</Button>
        </Link>
        <Button onClick={register}>Submit</Button>
      </Form> */}
      <section class="ftco-section">
        <div class="container">
          <div class="row justify-content-center">
            <div class="col-md-6 text-center mb-5">
              <h2 class="heading-section">register</h2>
            </div>
          </div>
          <div class="row justify-content-center">
            <div class="col-md-6 col-lg-5">
              <div class="login-wrap p-4 p-md-5 bg-light">
                <div class="icon d-flex align-items-center justify-content-center">
                  <img className="logo" src={Logo} alt="Logo" />
                </div>
                <h3 class="text-center mb-4">Register an account?</h3>
                <form action="#" class="login-form">
                  <div class="form-group">
                    <input
                      type="text"
                      class="form-control rounded-left mb-4 p-4"
                      placeholder="Username"
                      required=""
                    />
                  </div>
                  <div class="form-group d-flex">
                    <input
                      type="password"
                      class="form-control rounded-left mb-4 p-4"
                      placeholder="Password"
                      required=""
                    />
                  </div>
                  <div class="form-group d-flex">
                    <input
                      type="password"
                      class="form-control rounded-left mb-4 p-4"
                      placeholder="Password"
                      required=""
                    />
                  </div>
                  <div class="form-group d-md-flex">
                    <div class="w-50">
                      <label class="checkbox-wrap checkbox-primary">
                        Remember Me
                        <input type="checkbox" checked="" />
                        <span class="checkmark"></span>
                      </label>
                    </div>
                    <div class="w-50 text-md-right">
                      <Link to="/register">Register</Link>
                    </div>
                  </div>
                  <div class="form-group d-flex justify-content-between">
                    <Button className="m-6 w-100" onClick={register}>
                      Sign up
                    </Button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Register;
