import React from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Link } from "react-router-dom";
import { LoginUser } from "./UserBehavior";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
export default function Login() {
  useEffect(() => {
    document.title = "Login";
  });

  const [loginForm, setLoginForm] = useState({
    username: "",
    password: "",
  });
  const nav = useNavigate();
  const onChangeLogin = (event) =>
    setLoginForm({ ...loginForm, [event.target.name]: event.target.value });

  const Login = async (event) => {
    event.preventDefault();
    try {
      const LoginData = await LoginUser(loginForm);
      if (LoginData.success) {
        nav("/Dashboard");
      }
    } catch (error) {
      console.log(error.response);
    }
  };

  return (
    <>
      {/* <Form onSubmit={Login}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>username</Form.Label>
          <Form.Control
            name="username"
            onChange={onChangeLogin}
            value={loginForm.username}
            type="text"
            placeholder="Enter email"
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            name="password"
            onChange={onChangeLogin}
            value={loginForm.password}
            type="password"
            placeholder="Password"
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>
        <Link to="/register">
          <Button>register</Button>
        </Link>
        <Button onClick={Login}>Submit</Button>
      </Form> */}
      <div className="container">
        <hr className="mt-2 mb-5"></hr>
        <div className="mt-5">
          <div className={`row justify-content-center ${classValidated}`}>
            <form
              className="col-12 col-md-6 text-center shadow rounded p-4 bg-light"
              onSubmit={handleClickSigninButton}
              id="form-login"
              noValidate
            >
              <img
                src={Img}
                width="70"
                height="70"
                className="border rounded-circle p-1 m-3"
                alt="logo Coffeein"
              />
              <h1 className="h3 mb-3 pb-4 fw-normal">Please sign in</h1>
              <ErrorBox messageError={Error.E001} showErrorBox={showErrorBox} />
              <InputField
                id="username"
                label="Username"
                handleChangeValue={handleChangeUsername}
                isShowError
                messageError={Error.E002}
                required
              />
              <InputField
                id="password"
                label="Password"
                handleChangeValue={handleChangePassword}
                isShowError
                messageError={Error.E003}
                type="password"
                required
              />

              <button
                className="w-25 btn btn-lg btn-primary mx-5"
                type="submit"
              >
                Sign in
              </button>
              <button
                className="w-25 btn btn-lg btn-primary mx-5"
                type="button"
                onClick={handleClickBackButton}
              >
                Back
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
