import initData from "./Data";
import { DragDropContext } from "react-beautiful-dnd";
import Column from "./Column";
import { useState } from "react";
const LayoutDND = () => {
  const [state, setstate] = useState(initData);
  const results = {
    draggableId: "task-1",
    type: "TYPE",
    reason: "DROP",
    source: {
      droppableId: "colum-1",
      index: 0,
    },
    destination: null,
  };

  const onDragEnd = ({ results }) => {
    const { destination, draggableId, source } = results;
    if(!destination){return;}
    if(destination.droppableId === source.droppableId && destination.index === source.index){
      return;
    }
  };
  return (
    <>
      <DragDropContext onDragEnd={onDragEnd(...results)}>
        {state.columOrder.map((columID) => {
          const colum = state.colums[columID];
          const tasks = colum.taskIds.map((taskIds) => state.tasks[taskIds]);
          console.log(colum);
          return <Column key={colum.id} column={colum} tasks={tasks}></Column>;
        })}
      </DragDropContext>
    </>
  );
};
export default LayoutDND;
