import React from "react";
import Button from "react-bootstrap/Button";

import { useNavigate, Link } from "react-router-dom";
import { useState } from "react";
import { RegisterUser } from "./UserBehavior";
import ErrorBox from "./Errorbox";
import Logo from "../Login/user.jpg";
const Register = () => {
  const [showErrorBox, setShowErrorBox] = useState(false);
  let nav = useNavigate();
  const [formRegister, changeRegister] = useState({
    username: "",
    password: "",
    repassword: "",
  });
  const handleRegister = (event) => {
    changeRegister({
      ...formRegister,
      [event.target.name]: event.target.value,
    });
    setShowErrorBox(false);
  };
  const register = async () => {
    if (formRegister.password !== formRegister.repassword) {
      alert("Password không trùng khớp");
      return setShowErrorBox(true);
    }
    try {
      const registerUser = await RegisterUser(formRegister);
      if (registerUser.success) {
        alert("Tài khoản đã tồn tại");
        nav("/login");
      }
    } catch (error) {
      alert("Tài khoản đã tồn tại");
      setShowErrorBox(true);
      console.log(error.response);
    }
  };

  return (
    <>
      <div class="overbox">
        <div class="material-button alt-2">
          <span class="shape"></span>
        </div>

        <div class="title">REGISTER</div>
        <div class="input">
          <label for="regname">Username</label>
          <input
            type="text"
            name="username"
            onChange={handleRegister}
            value={formRegister.username}
            id="regname"
          />
          <span class="spin"></span>
        </div>

        <div class="input">
          <label for="regpass">Password</label>
          <input
            type="password"
            name="password"
            onChange={handleRegister}
            value={formRegister.password}
            id="regpass"
          />
          <span class="spin"></span>
        </div>

        <div class="input">
          <label for="reregpass">Repeat Password</label>
          <input
            type="password"
            name="repassword"
            onChange={handleRegister}
            value={formRegister.repassword}
            id="reregpass"
          />
          <span class="spin"></span>
        </div>

        <div class="button ">
          <Button className="m-6 w-100" onClick={register}>
            Sign up
          </Button>
        </div>
      </div>
    </>
  );
};
export default Register;
