import React from "react";
import Button from "react-bootstrap/Button";

import { useNavigate, Link } from "react-router-dom";
import { useState } from "react";
import { RegisterUser } from "./UserBehavior";
import ErrorBox from "./Errorbox";
import Logo from "../Login/user.jpg";
const Register = () => {
  const [showErrorBox, setShowErrorBox] = useState(false);
  let nav = useNavigate();
  const [formRegister, changeRegister] = useState({
    username: "",
    password: "",
    repassword: "",
  });
  const handleRegister = (event) => {
    changeRegister({
      ...formRegister,
      [event.target.name]: event.target.value,
    });
    setShowErrorBox(false);
  };
  const register = async () => {
    if (formRegister.password !== formRegister.repassword) {
      return setShowErrorBox(true);
    }
    try {
      const registerUser = await RegisterUser(formRegister);
      if (registerUser.success) {
        nav("/login");
      }
    } catch (error) {
      setShowErrorBox(true);
      console.log(error.response);
    }
  };
  return (
    <>
      <section className="ftco-section">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-6 text-center mb-5"></div>
          </div>
          <div className="row justify-content-center">
            <div className="col-md-6 col-lg-5">
              <div className="login-wrap p-4 p-md-5 bg-light">
                <div className="icon d-flex align-items-center justify-content-center">
                  <img className="logo" src={Logo} alt="Logo" />
                </div>
                <h3 className="text-center mb-4">Register an account?</h3>
                <ErrorBox
                  messageError={"Thông tin không hợp lệ"}
                  showErrorBox={showErrorBox}
                />
                <form action="#" className="login-form">
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-control rounded-left mb-4 p-4"
                      placeholder="Username"
                      required=""
                      name="username"
                      onChange={handleRegister}
                      value={formRegister.username}
                    />
                  </div>
                  <div className="form-group d-flex">
                    <input
                      type="password"
                      className="form-control rounded-left mb-3 p-4"
                      placeholder="Password"
                      required=""
                      name="password"
                      onChange={handleRegister}
                      value={formRegister.password}
                    />
                  </div>
                  <div className="form-group d-flex">
                    <input
                      type="password"
                      className="form-control rounded-left mb-4 p-4"
                      placeholder="Password"
                      required=""
                      name="repassword"
                      onChange={handleRegister}
                      value={formRegister.repassword}
                    />
                  </div>
                  <div className="form-group d-md-flex">
                    <div className="w-50">
                      <label className="checkbox-wrap checkbox-primary">
                        Remember Me
                        <input type="checkbox" checked="" />
                        <span className="checkmark"></span>
                      </label>
                    </div>
                    <div className="w-50 text-md-right">
                      <Link to="/login">register</Link>
                    </div>
                  </div>
                  <div className="form-group d-flex justify-content-between">
                    <Button className="m-6 w-100" onClick={register}>
                      Sign up
                    </Button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Register;
