import React from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useNavigate, Link } from "react-router-dom";
import { useState } from "react";
import { RegisterUser } from "./UserBehavior";
const Register = () => {
  const [alert, setAlert] = useState(null);
  let nav = useNavigate();
  const [formRegister, changeRegister] = useState({
    username: "",
    password: "",
    repassword: "",
  });
  const handleRegister = (event) => {
    changeRegister({
      ...formRegister,
      [event.target.name]: event.target.value,
    });
  };
  const register = async () => {
    if (formRegister.password !== formRegister.repassword) {
      return setAlert({ type: "danger", message: "Password Don't match" });
    }
    try {
      const registerUser = await RegisterUser(formRegister);
      if (registerUser.success) {
        return nav("/Dashboard");
      }
    } catch (error) {
      console.log(error.response);
    }
  };
  return (
    <>
      <h1>Register</h1>
      <Form onSubmit={register}>
        <h2>{alert}</h2>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>username</Form.Label>
          <Form.Control
            name="username"
            onChange={handleRegister}
            value={formRegister.username}
            type="text"
            placeholder="Enter email"
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            name="password"
            onChange={handleRegister}
            value={formRegister.password}
            type="password"
            placeholder="Password"
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Re-Password</Form.Label>
          <Form.Control
            name="repassword"
            onChange={handleRegister}
            value={formRegister.repassword}
            type="password"
            placeholder="Password"
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>
        <Link to="/register">
          <Button>register</Button>
        </Link>
        <Button onClick={register}>Submit</Button>
      </Form>
    </>
  );
};
export default Register;
