import Axios from "axios";

export const LoginUser = async (req) => {
  const respod = await Axios.post("http://localhost:8080/Auth/Login", req);
  console.log(respod);
  if (respod.data.success) {
    localStorage.setItem("token", respod.data.Accesstoken);
    localStorage.setItem("username", respod.data.user.username);
    localStorage.setItem("userid", respod.data.user._id);
    localStorage.setItem("codeShare", respod.data.user._id);
  }

  return respod.data;
};

export const RegisterUser = async (req) => {
  console.log(req);
  const respod = await Axios.post("http://localhost:8080/Auth/", req);
  return respod.data;
};
