import React from "react";
import Button from "react-bootstrap/Button";

import { Link } from "react-router-dom";
import { LoginUser } from "./UserBehavior";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Logo from "../Login/user.jpg";
import ErrorBox from "./Errorbox";
import "./StyleScss.scss";

export default function Login() {
  const [showErrorBox, setShowErrorBox] = useState(false);
  const nav = useNavigate();
  useEffect(() => {
    document.title = "Login";
  });
  const [loginForm, setLoginForm] = useState({
    username: "",
    password: "",
  });

  const onChangeLogin = (event) => {
    setLoginForm({ ...loginForm, [event.target.name]: event.target.value });
    setShowErrorBox(false);
  };

  const Login = async (event) => {
    event.preventDefault();
    try {
      const LoginData = await LoginUser(loginForm);
      if (LoginData.success) {
        nav("/Dashboard");
      }
    } catch (error) {
      setShowErrorBox(true);
      console.log(error.response);
    }
  };

  return (
    <>
      <form>
        <div class="svgContainer">
          <div>
            <svg
              class="mySVG"
              xmlns="http://www.w3.org/2000/svg"
              xmlnsXlink="http://www.w3.org/1999/xlink"
              viewBox="0 0 200 200"
            >
              asd
            </svg>
          </div>
        </div>

        <div class="inputGroup inputGroup1">
          <label for="loginEmail" id="loginEmailLabel">
            Email
          </label>
          <input type="email" id="loginEmail" maxlength="254" />
          <p class="helper helper1">email@domain.com</p>
        </div>
        <div class="inputGroup inputGroup2">
          <label for="loginPassword" id="loginPasswordLabel">
            Password
          </label>
          <input type="password" id="loginPassword" />
          <label id="showPasswordToggle" for="showPasswordCheck">
            Show
            <input id="showPasswordCheck" type="checkbox" />
            <div class="indicator"></div>
          </label>
        </div>
        <div class="inputGroup inputGroup3">
          <button id="login">Log in</button>
        </div>
      </form>
    </>
  );
}
