import { Draggable } from "react-beautiful-dnd";
import styled from "styled-components";
import { getOnePost } from "../Dashboard/postBehavior";
import { useState } from "react";
const Container = styled.div`
  padding: 30px;
  border: solid 2px;
  display: flex;
  align-items: center;
  background-color: ${(props) => (props.isDragging ? "blue" : "white")};
`;
const Handle = styled.div`
  background-image: url("https://toppng.com/uploads/preview/drag-ico-11569048140clkfdt3vrw.png");
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  width: 43px;
  height: 36px;
  border-radius: 13px;
  margin-right: 20px;
`;

const onclickGetonePostcolor = async (task) => {
  const getOneP = await getOnePost(task);

  return getOneP.posts;
};

const Task = ({ task, index }) => {
  let color = onclickGetonePostcolor(task);

  const [color2, setcolor] = useState({});

  color.then(function (result) {
    setcolor(result);
  });

  console.log("color", color2);
  return (
    <>
      <Draggable draggableId={task} index={index}>
        {(provider, snapshot) => (
          <Container
            {...provider.draggableProps}
            isDragging={snapshot.isDragging}
            ref={provider.innerRef}
          >
            <Handle {...provider.dragHandleProps}></Handle>
            {color2.title} -{color2.color}
          </Container>
        )}
      </Draggable>
    </>
  );
};
export default Task;
