import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { Navbar } from "../Layout/Navbar";
import { Map } from "../Layout/Caroucel";
import Button from "react-bootstrap/esm/Button";
import React from "react";
import { NotificationContainer } from "react-notifications";
import { createNotification } from "../Notifications/notifications";
import "react-notifications/lib/notifications.css";
import LayoutDNDConnectDB from "../LayoutConnectDB/Layout";
import {
  getPosts,
  createnewPost,
  DeletedPost,
  getOnePost,
  EditPost,
} from "./postBehavior";
import { ModalEdit } from "./handleDash";
export const Dashboard = () => {
  const [Post, setPost] = useState();
  let nav = useNavigate();

  const [createPost, setCreatePost] = useState({
    title: "",
    Des: "",
    Url: "",
    Behavior: "Learning",
    color: "#ffa500",
    user: localStorage.getItem("userid"),
  });

  // Edit Post
  let [getPostEdit, setGetPostEdit] = useState(" ");

  const onchangeEdit = (event) => {
    setGetPostEdit({
      ...getPostEdit,
      [event.target.name]: event.target.value,
    });
  };

  const onSubmitEdit = async (res) => {
    try {
      const GetEdit = await EditPost(getPostEdit);
      if (GetEdit.data.success) {
        createNotification("warning", GetEdit.data.Message);
      }
    } catch (error) {
      createNotification("error", "Title can't be empty");
    }
  };

  const onchangeCreate = (event) => {
    setCreatePost({
      ...createPost,
      [event.target.name]: event.target.value,
    });
  };

  const getallPost = async () => {
    try {
      const GetallPostzx = await getPosts(localStorage.getItem("token"));
      if (GetallPostzx.success) {
        setPost(GetallPostzx.Post_of_User);
      }
    } catch (error) {
      console.log(error);
    }
  };
  const onclickGetonePost = async (event) => {
    const getOneP = await getOnePost(event.target.value);
    if (getOneP.success) {
      setGetPostEdit(getOneP.posts);
      getPostEdit = getOneP.posts;
    }
  };

  const onSubmitCreate = async (event) => {
    event.preventDefault();
    try {
      const onCreate = await createnewPost(createPost);
      if (onCreate.data.success) {
        createNotification("success", "Táº¡o post má»›i thÃ nh cÃ´ng");
      }
    } catch (error) {
      createNotification("error", "Title can't be empty");
    }
  };
  const RemovePost = async (event) => {
    await DeletedPost(event.target.value);
  };
  useEffect(() => {
    if (localStorage.getItem("token") === null) {
      return nav("/login");
    }
    getallPost();
  });
  return (
    <>
      <Navbar></Navbar>
      <Map></Map>
      <NotificationContainer></NotificationContainer>
      <div className="managePost">
        <span className="managePost-title">
          <Button
            type="button"
            className="btn btn-primary"
            data-bs-toggle="modal"
            data-bs-target="#staticBackdrop"
          >
            Add post
          </Button>
        </span>
        <div
          className="modal fade"
          id="staticBackdrop"
          data-bs-backdrop="static"
          data-bs-keyboard="false"
          aria-labelledby="staticBackdropLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="staticBackdropLabel">
                  Add new post
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                ></button>
              </div>
              <div className="modal-body">
                <form>
                  <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">
                      Title
                    </label>
                    <input
                      type="text"
                      name="title"
                      onChange={onchangeCreate}
                      value={createPost.title}
                      class="form-control"
                      id="exampleInputEmail1"
                      aria-describedby="emailHelp"
                    />
                    <div id="emailHelp" class="form-text"></div>
                  </div>
                  <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">
                      Describtion
                    </label>
                    <input
                      name="Des"
                      type="text"
                      onChange={onchangeCreate}
                      value={createPost.Des}
                      class="form-control"
                      id="exampleInputPassword1"
                    />
                  </div>
                  <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">
                      Url
                    </label>
                    <input
                      name="Url"
                      type="text"
                      onChange={onchangeCreate}
                      value={createPost.Url}
                      placeholder="https://"
                      class="form-control"
                      id="exampleInputPassword1"
                    />
                  </div>
                  <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">
                      Behavior
                    </label>
                    <select
                      name="Behavior"
                      class="form-select"
                      onChange={onchangeCreate}
                      aria-label="Default select example"
                    >
                      <option value="To-Learning">To-Learning</option>
                      <option value="Learning">Learning</option>
                    </select>
                  </div>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      data-bs-dismiss="modal"
                    >
                      Cancel
                    </button>
                    <Button
                      onClick={onSubmitCreate}
                      className="btn btn-primary"
                    >
                      Submit
                    </Button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
        {/* modal edit */}
      </div>
      <div>
        <form st>
          <div className="managePost-body">
            <ul className="body-Listitem">
              {Post &&
                Post.map(({ title, _id, Url, Des, user, color }) => (
                  <li
                    key={_id}
                    id={_id}
                    name="id"
                    style={{ backgroundColor: `${color}` }}
                    className="body-item"
                  >
                    <Button onClick={RemovePost} value={_id}>
                      X
                    </Button>
                    <Button
                      type="button"
                      className="btn btn-primary"
                      data-bs-toggle="modal"
                      data-bs-target="#staticBackdrop2"
                      onClick={onclickGetonePost}
                      value={_id}
                    >
                      Edit
                    </Button>
                    <Button
                      onClick={() => {
                        window.open(Url);
                      }}
                    >
                      ðŸ”—
                    </Button>
                    <ModalEdit
                      id={_id}
                      color={color}
                      onchangeEdit={onchangeEdit}
                      onSubmitEdit={onSubmitEdit}
                      getPostEdit={getPostEdit}
                    ></ModalEdit>
                    {title} - {Url} - {Des} -{color}
                  </li>
                ))}
            </ul>
          </div>
        </form>
      </div>
      <LayoutDNDConnectDB></LayoutDNDConnectDB>
    </>
  );
};
