import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { Navbar } from "../Layout/Navbar";
import { Map } from "../Layout/Caroucel";
import Button from "react-bootstrap/esm/Button";
import React from "react";
import { NotificationContainer } from "react-notifications";
import { createNotification } from "../Notifications/notifications";
import "react-notifications/lib/notifications.css";
import {
  getPosts,
  createnewPost,
  DeletedPost,
  getOnePost,
  EditPost,
} from "./postBehavior";
import { udlus } from "../Login/UserBehavior";
import { CheckLoginUser } from "../Login/UserBehavior";
import ReactTooltip from "react-tooltip";
import { ModalEdit, ModalAddpost } from "./handleDash";
import { FormSendMail } from "../SendEmail/Sendmail";
import { decodeToken } from "react-jwt";
import "./style.css";
import * as Icon from "react-bootstrap-icons";
export const Dashboard = () => {
  const [Post, setPost] = useState();
  const [clickOnetimes, setClickOnetimes] = useState(false);
  const [ListUserAccess, setListUserAccess] = useState([]);
  const [ListUserRequest, setListUserRequest] = useState([]);
  let myDecodedToken = decodeToken(localStorage.getItem("CodeShare"));

  let nav = useNavigate();

  const getListfromUser = async () => {
    let FormAccessUser = {
      username: myDecodedToken.CODESHARE.split("#")[1],
      password: myDecodedToken.CODESHARE.split("#")[0],
    };
    const getUser = await CheckLoginUser(FormAccessUser);
    setListUserAccess(getUser.user.ListEmailJoin);
    setListUserRequest(getUser.user.ListEmailRequestJoin);
  };
  const [createPost, setCreatePost] = useState({
    title: "",
    Des: "",
    Url: "",
    Behavior: "Learning",
    color: "#ffa500",
    user: localStorage.getItem("userid"),
  });

  // Edit Post
  let [getPostEdit, setGetPostEdit] = useState("");

  const onchangeEdit = (event) => {
    setGetPostEdit({
      ...getPostEdit,
      [event.target.name]: event.target.value,
    });
  };

  const onSubmitEdit = async (res) => {
    try {
      const GetEdit = await EditPost(getPostEdit);
      if (GetEdit.data.success) {
        createNotification("warning", GetEdit.data.Message);
      }
    } catch (error) {
      createNotification("error", "Title can't be empty");
    }
  };

  const onchangeCreate = (event) => {
    setCreatePost({
      ...createPost,
      [event.target.name]: event.target.value,
    });
  };

  const getallPost = async () => {
    try {
      const GetallPostzx = await getPosts(localStorage.getItem("token"));
      if (GetallPostzx.success) {
        setPost(GetallPostzx.Post_of_User);
      }
    } catch (error) {
      console.log(error);
    }
  };
  const onclickGetonePost = async (event) => {
    const getOneP = await getOnePost(event.target.value);
    if (getOneP.success) {
      setGetPostEdit(getOneP.posts);
      getPostEdit = getOneP.posts;
    }
  };

  const onSubmitCreate = async (event) => {
    event.preventDefault();
    try {
      const onCreate = await createnewPost(createPost);
      if (onCreate.data.success) {
        createNotification("success", "Create new post success");
      }
    } catch (error) {
      createNotification("error", "Title can't be empty");
    }
  };
  const RemovePost = async (event) => {
    await DeletedPost(event.target.value);
  };
  useEffect(() => {
    if (localStorage.getItem("token") === null) {
      return nav("/login");
    }
    getallPost();
    getListfromUser();
  });
  const RemoveUserAccess = async (event) => {
    let layremove = [];
    layremove = ListUserAccess;
    console.log("Before remove", layremove);
    const index = layremove.indexOf(event.target.value);
    if (index > -1) {
      layremove.splice(index, 1);
    }
    let FormUpdateListUser = {
      _id: localStorage.getItem("userid"),
      ListEmailJoin: layremove,
    };
    await udlus(FormUpdateListUser);
    console.log("Afer remove", layremove);
  };
  return (
    <>
      <FormSendMail
        CodeShare={myDecodedToken.CODESHARE}
        email={localStorage.getItem("email")}
      ></FormSendMail>
      <Navbar></Navbar>
      <Map></Map>
      <NotificationContainer></NotificationContainer>
      <div className="managePost">
        <span style={{ marginLeft: "12px" }} className="managePost-title">
          <Button
            type="button"
            className="btn btn-primary"
            data-bs-toggle="modal"
            data-bs-target="#staticBackdrop"
          >
            Add post
          </Button>

          <a style={{ width: "100%", marginLeft: "20px" }} href="/layoutdb">
            <Button>Go to work-space</Button>
          </a>
          <Button
            style={{ marginLeft: "20px" }}
            class="btn btn-primary"
            type="button"
            data-bs-toggle="offcanvas"
            data-bs-target="#offcanvasScrolling"
            aria-controls="offcanvasScrolling"
          >
            Manager user
          </Button>

          <div
            class="offcanvas offcanvas-start"
            data-bs-scroll="true"
            data-bs-backdrop="false"
            tabindex="-1"
            id="offcanvasScrolling"
            aria-labelledby="offcanvasScrollingLabel"
          >
            <div class="offcanvas-header">
              <h5 class=" offcanvas-title" id="offcanvasScrollingLabel">
                Offcanvas with body scrolling
              </h5>
              <button
                type="button"
                class="btn-close"
                data-bs-dismiss="offcanvas"
                aria-label="Close"
              ></button>
            </div>

            <div class="offcanvas-body">
              <div class="FormUserAccess">
                <div class="FormUserAccess-title">
                  <h5> list of users with access rights</h5>
                </div>
                <ul class="FormUserAccess-list">
                  {ListUserAccess &&
                    ListUserAccess.map((ListUserAccess) => (
                      <li class="FormUserAccess-list-iteam">
                        <div class="FormUserAccess-list-iteam_left">
                          {ListUserAccess}
                        </div>
                        <div class="FormUserAccess-list-iteam_Right">
                          <button
                            value={ListUserAccess}
                            onClick={RemoveUserAccess}
                            type="button"
                            class="btn btn-danger mt-1"
                          >
                            x
                          </button>
                        </div>
                      </li>
                    ))}
                </ul>
              </div>
              <hr></hr>
              <div class="FormUserRequestAccess">
                <div class="FormUserRequestAccess-title">
                  <h5>Email sent approval request</h5>
                </div>
                <ul class="FormUserRequestAccess-list">
                  {ListUserRequest &&
                    ListUserRequest.map((ListUserAccess) => (
                      <li class="FormUserRequestAccess-list-iteam">
                        <div class="FormUserRequestAccess-list-iteam_left">
                          {ListUserAccess}
                        </div>
                        <div class="FormUserRequestAccess-list-iteam_Right">
                          <button type="button" class="btn btn-success mt-1">
                            <Icon.Check></Icon.Check>
                          </button>
                          &ensp;
                          <button type="button" class="btn btn-danger mt-1">
                            x
                          </button>
                        </div>
                      </li>
                    ))}
                </ul>
              </div>
            </div>
          </div>

          <Button
            data-tip="Coppy code share"
            className="btn-info"
            onClick={() => {
              const copyText = document.getElementById("myInput");
              navigator.clipboard.writeText(copyText.value);
              setClickOnetimes(true);
            }}
            style={{ marginLeft: "20px" }}
          >
            <ReactTooltip style={{ marginLeft: "50px" }} />
            {clickOnetimes ? (
              <Icon.Check size={25}></Icon.Check>
            ) : (
              <Icon.Clipboard2 size={25}></Icon.Clipboard2>
            )}
          </Button>

          <input
            type="hidden"
            value={localStorage.getItem("CodeShare")}
            id="myInput"
          ></input>
        </span>
        <ModalAddpost
          onchangeCreate={onchangeCreate}
          createPost={createPost}
          onSubmitCreate={onSubmitCreate}
        ></ModalAddpost>
        <form>
          <div className="managePost-body">
            <ul className="body-Listitem">
              {Post &&
                Post.map(({ title, _id, Url, Des, user, color }) => (
                  <li
                    key={_id}
                    id={_id}
                    name="id"
                    style={{ backgroundColor: `${color}` }}
                    className="body-item"
                  >
                    <Button onClick={RemovePost} value={_id}>
                      X
                    </Button>
                    <Button
                      type="button"
                      className="btn btn-primary"
                      data-bs-toggle="modal"
                      data-bs-target="#staticBackdrop2"
                      onClick={onclickGetonePost}
                      value={_id}
                    >
                      Edit
                    </Button>
                    <Button
                      onClick={() => {
                        window.open(Url);
                      }}
                    >
                      🔗
                    </Button>
                    <ModalEdit
                      id={_id}
                      color={color}
                      onchangeEdit={onchangeEdit}
                      onSubmitEdit={onSubmitEdit}
                      getPostEdit={getPostEdit}
                    ></ModalEdit>

                    <p className="cssforItem-content">
                      <b>Title: {title}</b>
                    </p>
                    <p className="cssforItem-content">
                      URL:
                      {Url && Url.length > 20
                        ? `${Url.substr(0, 20)}...`
                        : `${Url}`}
                    </p>
                    <p className="cssforItem-content">
                      DES:
                      {Des && Des.length > 20
                        ? `${Des.substr(0, 19)}...`
                        : `${Des}`}
                    </p>
                  </li>
                ))}
            </ul>
          </div>
        </form>
      </div>
    </>
  );
};
