import { Responsive as ResponsiveGridLayout } from "react-grid-layout";
import GridLayout from "react-grid-layout";
import RGL, { WidthProvider } from "react-grid-layout";
import "./style.css";

import { useEffect, useState } from "react";
const ReactGridLayout = WidthProvider(RGL);
export const Layoutgrid = () => {
  // {lg: layout1, md: layout2, ...}
  const [layout1, setlayout1] = useState({
    layout: [],
  });
  const defaultProps = {
    className: "layout",
    cols: 12,
    rowHeight: 30,
    onLayoutChange: function () {},
  };
  const onLayoutChange = (layout) => {
    setlayout1({ layout });
    // onLayoutChange(layout);
  };

  const layoutvc = [
    { i: "a", x: 0, y: 0, w: 2, h: 2 },
    { i: "b", x: 1, y: 2, w: 1, h: 2 },
    { i: "c", x: 4, y: 0, w: 1, h: 2 },
  ];

  const users = [
    {
      id: 1,
      name: "Bùi Văn Tèo",
      age: 25,
    },
    {
      id: 2,
      name: "Trần Văn An",
      age: 29,
    },
    {
      id: 3,
      name: "Nguyễn Thị Bưởi",
      age: 22,
    },
  ];

  useEffect(() => {
    console.log("layout", layout1);
  });
  return (
    <>
      <div>
        <ReactGridLayout
          {...defaultProps}
          layout={layout1.layout}
          onLayoutChange={onLayoutChange}
        >
          {/* <div key="abc" data-grid={layout[0]}>
            <span className="text">1</span>
          </div> */}
          {/* <div key="2" data-grid={{ w: 2, h: 3, x: 2, y: 0 }}>
            <span className="text">2</span>
          </div>
          <div key="3" data-grid={{ w: 2, h: 3, x: 4, y: 0 }}>
            <span className="text">3</span>
          </div>
          <div key="4" data-grid={{ w: 2, h: 3, x: 6, y: 0 }}>
            <span className="text">4</span>
          </div>
          <div key="5" data-grid={{ w: 2, h: 3, x: 8, y: 0 }}>
            <span className="text">5</span>
          </div> */}
          {layoutvc.map((item) => (
            <div key={item.i} >
              <span className="text">5</span>
            </div>
          ))}
        </ReactGridLayout>
      </div>
    </>
  );
};
