import React from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Link } from "react-router-dom";
import { LoginUser } from "./UserBehavior";
import { useState, useEffect } from "react";

export default function Login() {
  const [loginForm, setLoginForm] = useState({
    username: "a",
    password: "",
  });

  const onChangeLogin = (event) => {
    setLoginForm({ ...loginForm, [event.target.name]: event.target.value });
  };

  return (
    <>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control name="username" onChange={onChangeLogin} value={loginForm.username} type="email" placeholder="Enter email" />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control name="password" onChange={onChangeLogin} value={loginForm.password} type="password" placeholder="Password" />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>
        <Link to="/register">
          <Button>register</Button>
        </Link>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </>
  );
}
