import { Draggable } from "react-beautiful-dnd";
import styled from "styled-components";
import { getOnePost,DeletedColumns_ByID } from "../Dashboard/postBehavior";
import { useState } from "react";
const Container = styled.div`
  padding: 30px;
  border: solid 2px;
  display: flex;
  background-color: ${(props) => (props.isDragging ? "blue" : "white")};
`;
const Handle = styled.div`
  background-color: orange;
  width: 43px;
  height: 28px;
  border-radius: 13px;
  margin-right: 20px;
`;
const onclickGetonePost = async (task) => {
  const getOneP = await getOnePost(task);
  return getOneP.posts.title;
};
const DeltedById = async (id) => {
  const ShowDelted = await DeletedColumns_ByID(id);
};
const Task = ({ task, index }) => {
  // let taskname = onclickGetonePost(task);
  // const [taskname2, setTaskname] = useState("");
  // taskname.then(function (result) {
  //   setTaskname(result);
  // });
  return (
    <>
      <Draggable draggableId={task} index={index}>
        {(provider, snapshot) => (
          <Container
            {...provider.draggableProps}
            isDragging={snapshot.isDragging}
            ref={provider.innerRef}
          >
            <Handle {...provider.dragHandleProps}></Handle>
            {task}
            <div on>X</div>
          </Container>
        )}
      </Draggable>
    </>
  );
};
export default Task;
